const l=JSON.parse('{"key":"v-22414950","path":"/en/train/eda/chip-circuit/Part_3-chip_flow/3_2_floorplan_pdn.html","title":"3.2 布图规划与电源规划","lang":"en-US","frontmatter":{"title":"3.2 布图规划与电源规划","order":2},"headers":[{"level":2,"title":"1 布图规划","slug":"_1-布图规划","link":"#_1-布图规划","children":[{"level":3,"title":"（1）布图规划的输入文件","slug":"_1-布图规划的输入文件","link":"#_1-布图规划的输入文件","children":[]},{"level":3,"title":"（2）布图规划的主要内容","slug":"_2-布图规划的主要内容","link":"#_2-布图规划的主要内容","children":[]},{"level":3,"title":"（3）布图规划和布局的区别","slug":"_3-布图规划和布局的区别","link":"#_3-布图规划和布局的区别","children":[]},{"level":3,"title":"（4）布图规划的目标","slug":"_4-布图规划的目标","link":"#_4-布图规划的目标","children":[]}]},{"level":2,"title":"2 电源规划","slug":"_2-电源规划","link":"#_2-电源规划","children":[{"level":3,"title":"（1）电源规划的目标","slug":"_1-电源规划的目标","link":"#_1-电源规划的目标","children":[]},{"level":3,"title":"（2）电源网络介绍","slug":"_2-电源网络介绍","link":"#_2-电源网络介绍","children":[]}]},{"level":2,"title":"引用","slug":"引用","link":"#引用","children":[]}],"git":{"createdTime":1723131714000,"updatedTime":1723131714000,"contributors":[{"name":"Xingquan-Li","email":"fzulxq@gmail.com","commits":1}]},"readingTime":{"minutes":9.21,"words":2763},"filePathRelative":"en/train/eda/chip-circuit/Part_3-chip_flow/3_2_floorplan_pdn.md","localizedDate":"August 8, 2024","excerpt":"<p>逻辑综合阶段输出了门级网表和设计约束文件（SDC），紧接着步入物理设计阶段。芯片设计的物理设计通常被简称为布局布线（PnR），其实不那么准确。在布局之前的大量工作包括 布图规划（floorplan） 和 电源规划(powerplan) 常常被忽略。</p>\\n<p>若把芯片制作与建房子做个不完全类比，布图规划相当于房子的初步设计图纸，大致确定建筑的形状大小以及户型；电源规划正如其名，规划建筑的总电源线布置；布局则正式确定每个户型的功能区，每个房间的地漏、电源插口等等的位置，以便于方便后续通管道（布线）。<strong>需要注意的是，布图规划与电源规划和布局三项任务通常是连续进行的，但在工程中往往是穿插反复进行。</strong></p>","copyright":{"author":"iEDA","license":"GPL-3.0"}}');export{l as data};
