import{_ as i,a as s}from"./iccad_time-d8cc3666.js";import{_ as l}from"./fig7-2-bfb0fe99.js";import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as d,o as c,c as D,a,w as n,d as e,e as o,f as _}from"./app-1ed3f6c2.js";const h={},A=e("h2",{id:"ieda团队勇夺2022年iccad竞赛第一名",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#ieda团队勇夺2022年iccad竞赛第一名","aria-hidden":"true"},"#"),o(" iEDA团队勇夺2022年ICCAD竞赛第一名")],-1),C=e("p",null,"2022年11月3日，在美国加州圣迭哥举行的电子设计自动化领域盛会ICCAD上揭晓了CAD Contest算法竞赛的最终排名。经过数月的激烈角逐，来自鹏城实验室开源EDA课题组的iEDA 3Dplacer团队获得了该项赛事三大赛道之一的第一名。本届CAD Contest@ICCAD算法竞赛共有来自国内外知名高校与研究机构的166支队伍参与，如东京大学、台湾大学、香港中文大学、北京大学、台湾清华大学、德州大学奥斯汀分校、复旦大学等。",-1),u=e("img",{src:i,alt:"6",style:{zoom:"30%"},title:"ICCAD第一名获奖证书"},null,-1),p=_('<h3 id="一、赛事背景" tabindex="-1"><a class="header-anchor" href="#一、赛事背景" aria-hidden="true">#</a> 一、赛事背景</h3><p>电子设计自动化（Electronic Design Automation，EDA）工具是指包括功能设计、逻辑综合、物理设计、仿真验证等一整套自动化芯片设计流程的计算机辅助设计软件。EDA作为集成电路产业链的战略基础支柱之一，积极促进芯片行业的技术革新，极大提升半导体产业效率。</p><p>国际计算机辅助设计会议（International Conference On Computer Aided Design，ICCAD）始于上世纪80年代，是电子设计自动化领域的顶级学术会议之一，其中CAD Contest@ICCAD算法竞赛是EDA领域影响范围最广、影响力最大的国际学术竞赛，竞赛赛题均来自Synopsys、Cadence、Siemens EDA、Nvidia、IBM、达摩院等全球著名EDA/IC公司的真实业务场景，期望针对当前工业界中所面临的亟需解决的实际问题研发出更好的解决方案。竞赛的结果可直接转化为工业界的解决方案，对集成电路计算机辅助设计领域的发展有很大的促进作用，每年都吸引工业界和学术界的高度关注。</p><p>本届CAD Contest@ICCAD算法竞赛共有来自国内外知名高校与研究机构的166支队伍参与，如东京大学、台湾大学、香港中文大学、北京大学、台湾清华大学、德州大学奥斯汀分校、复旦大学等。</p><h3 id="二、赛题解读" tabindex="-1"><a class="header-anchor" href="#二、赛题解读" aria-hidden="true">#</a> 二、赛题解读</h3><p>本届CAD Contest@ICCAD算法竞赛共发布三道赛题。由于课题组成员均涉足过物理设计相关研究，我们最终选择了研究背景更为契合的3D Die-to-Die Placement问题。Die-to-Die 技术是当前被使用得最广泛的 Chiplet 技术之一，是未来突破3D芯片设计的关键技术，其可以带来更高的良率、更好的时序、更低的成本等。而布局是物理设计中极其重要的一环，单元位置摆放的好坏在很大程度上直接影响芯片的PPA（Performance、Power、Area）。赛题要求参赛队伍在考虑上下层不同工艺、层利用率、混合键合端子数量和间距约束等诸多复杂约束的基础上，将网表划分成两部分，并确定每个单元在所属层中的具体位置，使得走线的总长度最短。解决该问题对提升3D芯片设计质量有关键的提升。</p><h3 id="三、竞赛历程" tabindex="-1"><a class="header-anchor" href="#三、竞赛历程" aria-hidden="true">#</a> 三、竞赛历程</h3><p>3D Die-to-Die布局对于课题组而言是崭新的，这让我们十分有兴趣去探索在这个新场景下自动化布局的方案。我们从6月底开始做赛题，到9月初最终提交代码，中间历经2个多月的时间。由于开始时间较晚，而又想冲刺好成绩，这就意味着我们要争分夺秒，做好时间规划和分工协作。</p>',8),m=e("img",{src:s,alt:"6",style:{zoom:"30%"},title:"竞赛时间轴"},null,-1),f=e("p",null,"参赛期间，我们不断涌现新的想法并快速实现，对于赛题求解和算法优化时刻充满热情，曾在多个深夜，依旧在群聊中交流着试错结果和新的想法。为了警醒与激励每个组员，我们还制定了一份分数记录表，用以与当时的第一名比较，不断刷新着我们的最佳记录。中间发生的紧张小插曲是，在最终版本提交的前几天，我们的服务器环境镜像被误删，这意味着我们的成果无法上传至主办方的服务器，并进一步导致分数不生效。发现问题后，我们立即投入到服务器的抢修中，幸好最终有惊无险。临提交前两天，由于疫情影响我们的公寓被管控。在这种环境下，我们也都没有懈怠，继续共同进行最终的性能提升与过程检查。我们最终能在众多优秀的参赛队伍中脱颖而出，这离不开课题组的支持和我们每个参赛队员的共同努力。另外，团队借助“鹏城云脑II”超强算力实现算法中关键参数的学习，最终获得第一名的成绩。",-1),E=e("h3",{id:"四、ieda课题组介绍",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#四、ieda课题组介绍","aria-hidden":"true"},"#"),o(" 四、iEDA课题组介绍")],-1),I=e("p",null,"课题组主要围绕开源EDA工具、智能化的芯片设计方法、开源EDA系统平台、开源EDA基准测试集等内容开展研究，打造四维一体的AI+EDA技术生态体系（问题、数据、平台、算力），发展开源开放的芯片设计解决方案，提升芯片设计效率，改善芯片设计质量，数量级降低芯片设计门槛，吸引和促进芯片领域的创新创业，培养芯片专业人才，推动芯片产业多样化发展。",-1),x=e("p",null,"课题组致力于研制数字芯片物理实现EDA工具链，形成产学研合作互通的技术研发平台，连接学术界和工业界，形成产业闭环的重要组成部分（创新想法->验证->学术转化->应用迭代->算法改进）。目标为给研界提供更敏捷高效的研发系统；给学界提供真实的人才培养训练平台；给产界输出更加直接有效的关键技术和人才。项目预期打通国产28nm工艺Netlist-GDSII的芯片物理实现，构建EDA数据集，支撑产业界打磨EDA工具，研界评测关键技术和训练智能模型等。当前已完成问题拆解、系统平台、打通110nm物理实现并完成流片验证。",-1),g=e("p",null,"本届CAD Contest@ICCAD竞赛，团队的指导老师是李兴权老师和黄志鹏博士，参赛学生分别来自于四个不同的单位和专业（陈仕健：鹏城实验室与中科院计算所联培博士，计算机科学与技术；赵雪岩：中科院计算所博士，计算机体系结构；李江考：闽南师范大学硕士，运筹学；邱奕杭：广东工业大学硕士，控制科学与工程）。他们大多是来课题组访问或实习的学生，均同时承担物理设计中部分点工具的研发。大家怀揣着对开源EDA的热爱及愿景，共同汇聚在鹏城实验室iEDA课题组，并充分发挥各自的学科背景优势，互补学习，共同进步。",-1),N=e("p",null,"在这里，iEDA课题组，诚挚邀请各位对开源EDA项目研发和使用感兴趣的朋友们共同参与，给与我们更多的建议与支持。有兴趣的朋友可以加入我们的微信交流群。",-1),b=e("img",{src:l,alt:"6",style:{zoom:"110%"},title:"开源iEDA社区群"},null,-1);function y(P,v){const t=d("center");return c(),D("div",null,[A,C,a(t,null,{default:n(()=>[u]),_:1}),a(t,null,{default:n(()=>[o(" Fig. 1：ICCAD第一名获奖证书 ")]),_:1}),p,a(t,null,{default:n(()=>[m]),_:1}),a(t,null,{default:n(()=>[o("Fig. 2：竞赛时间轴")]),_:1}),f,E,I,x,g,N,a(t,null,{default:n(()=>[b]),_:1}),a(t,null,{default:n(()=>[o("开源iEDA社区群")]),_:1})])}const z=r(h,[["render",y],["__file","ICCAD_contest.html.vue"]]);export{z as default};
