import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r as a,o as r,c as s,d as o,e,a as n,f as l}from"./app-1ed3f6c2.js";const c={},u=o("p",null,[o("strong",null,"Introduction to OSEDA Workshop and Community")],-1),p=o("p",null,"We believe that in the future, every chip designer can use open-source design tools (EDA) to design, validate, and manufacture open-source RISC-V chips just as easily as using open-source GNU toolchains to compile C code. To achieve this common goal, active contributors from the RISC-V open-source community and the EDA research field established the OSEDA community, dedicated to developing and promoting open-source EDA technologies and tools in the RISC-V community. The short-term goal of the OSEDA community is to attract over 1,000 developers and over 10,000 developers by 2025 to join the open-source EDA community and contribute to open-source projects.",-1),h=o("p",null,"To foster the exchange of ideas and collaboration between open-source EDA developers and users from around the world, the OSEDA community plans to hold the Third OSEDA Workshop on August 20, 2024, as part of the fourth RISC-V China Summit. This workshop will be held concurrently with the RISC-V China Summit.",-1),m={class:"hint-container info"},d=o("p",{class:"hint-container-title"},"Info",-1),g={href:"https://ieda.oscc.cc/",target:"_blank",rel:"noopener noreferrer"},b={href:"https://gitee.com/oscc-project",target:"_blank",rel:"noopener noreferrer"},f={href:"https://github.com/cnrv/OSEDA",target:"_blank",rel:"noopener noreferrer"},_={href:"https://www.wenjuan.com/s/UZBZJvFH93/",target:"_blank",rel:"noopener noreferrer"},k=l('<p><strong>Date and Location</strong></p><blockquote><p>Workshop date: August 20, 2024, 14:00 -- 17:00<br> Workshop location: Hangzhou</p></blockquote><p><strong>Organizers</strong></p><blockquote><p>Beijing Institute of Open Source Chips,<br> Institute of Computational Technology, CAS,<br> Pengcheng Laboratory,<br> iEDA Community</p></blockquote><p><strong>Program Committee</strong></p><blockquote><p><strong>Honorary Chair:</strong><br> Pingqiang Zhou, ShanghaiTech University,<br> Biwei Xie, Institute of Computational Technology, CAS, Pengcheng Laboratory,<br> Wei Wu, OSDT Community, PLCT Laboratory</p></blockquote><blockquote><p><strong>Chair:</strong><br> Xingquan Li, Pengcheng Laboratory,<br> Hao Wang, Institute of Computational Technology, CAS</p></blockquote><p><strong>Call for Papers</strong></p><p>Topics accepted for the workshop (including but not limited to)</p><ul><li>Latest achievements and advancements in EDA technology</li><li>Development and application of EDA-related academic theories and algorithm techniques</li><li>Contributions to open-source EDA projects, including new feature implementations, tool quality optimization, and community management</li><li>Techniques for sharing open-source EDA tools for chip design and manufacturing</li><li>Practical applications of open-source EDA in education</li></ul><p>Paper submission requirements:</p><div class="hint-container tip"><p class="hint-container-title">Tips</p><ul><li>Submissions must be sent via email to &quot;<a href="mailto:ieda.oscc@gmail.com">ieda.oscc@gmail.com</a>&quot;. The email must follow the following format:</li><li>Title: &quot;[OSEDA]-Paper Title-Speaker Last Name&quot;</li></ul><p><strong>Email content:</strong></p><ol><li>Paper title</li><li>Speaker name and affiliation (for students, please provide the school)</li><li>Paper abstract (200-500 words)</li><li>Speaker bio (100-300 words)</li></ol></div><p>Please note that this workshop is not an academic publication, and unpublished research findings should be submitted with caution.</p><p><strong>Important Dates</strong></p><div class="hint-container warning"><p class="hint-container-title">Note</p><blockquote><p>Submission deadline: August 8, 2024<br> Notification of acceptance: August 12, 2024<br> For any questions about paper submissions, please contact Xingquan Li: <a href="mailto:fzulxq@gmail.com">fzulxq@gmail.com</a>, Hao Wang: <a href="mailto:wanghao@ict.ac.cn">wanghao@ict.ac.cn</a>.</p></blockquote></div>',15);function v(S,y){const t=a("ExternalLinkIcon");return r(),s("div",null,[u,p,h,o("div",m,[d,o("blockquote",null,[o("ul",null,[o("li",null,[e("Community website: "),o("a",g,[e("https://ieda.oscc.cc/"),n(t)])])])]),o("blockquote",null,[o("ul",null,[o("li",null,[e("Code repository: "),o("ul",null,[o("li",null,[o("a",b,[e("https://gitee.com/oscc-project"),n(t)])]),o("li",null,[o("a",f,[e("https://github.com/cnrv/OSEDA"),n(t)])])])]),o("li",null,[e("Registration form "),o("ul",null,[o("li",null,[o("a",_,[e("https://www.wenjuan.com/s/UZBZJvFH93/"),n(t)])])])])])])]),k])}const C=i(c,[["render",v],["__file","third-OSEDA-workshop-24_notice.html.vue"]]);export{C as default};
